mapper.select:
select a.id id, a.yearMonth yearMonth, a.empName empName, a.deptName deptName, a.workName workName, b.dayWork + b.dayWorkHoliday + b.dayWorkLegal as normalDays, 10 as normalPrice, (b.dayWork + b.dayWorkHoliday + b.dayWorkLegal) * 10 as normalMoney, b.nightWork + b.nightWorkHoliay + b.nigthWorkLegal as overtimeDays, 5 as overtimePrice, (b.nightWork + b.nightWorkHoliay + b.nigthWorkLegal) * 5 as overtimeMoney, a.latheWorkerSubsidy as sum from t_Summary_Salary_Monthly a LEFT JOIN t_Emp_Salary_Monthly b on a.yearMonth = b.yearMonth and a.empName = b.empName where a.isDelete = 0 and a.latheWorkerSubsidy > 0
and a.empName like '%${empName}%'
and a.deptName = #{deptName}
and a.yearMonth = #{yearMonth}
order by a.createTime desc

select a.id id, a.yearMonth yearMonth, a.empName empName, a.deptName deptName, a.workName workName, b.dayWork + b.dayWorkHoliday + b.dayWorkLegal as normalDays, a.newLatheWorkerSubsidy as newLatheWorkerSubsidy from t_Summary_Salary_Monthly a LEFT JOIN t_Emp_Salary_Monthly b on a.yearMonth = b.yearMonth and a.empName = b.empName where a.isDelete = 0 and a.newLatheWorkerSubsidy > 0
and a.empName like '%${empName}%'
and a.deptName = #{deptName}
and a.yearMonth = #{yearMonth}
order by a.createTime desc

select a.id id, a.yearMonth yearMonth, a.empName empName, a.deptName deptName, a.workName workName, b.dayWork + b.dayWorkHoliday + b.dayWorkLegal as normalDays, a.basicSalary as basicSalary, a.percentSubsidy as percentSubsidy from t_Summary_Salary_Monthly a LEFT JOIN t_Emp_Salary_Monthly b on a.yearMonth = b.yearMonth and a.empName = b.empName where a.isDelete = 0 and a.percentSubsidy > 0
and a.empName like '%${empName}%'
and a.deptName = #{deptName}
and a.yearMonth = #{yearMonth}
order by a.createTime desc
