mapper.select:
SELECT id AS id, orderId as orderId, customerName as customerName, orderNo as orderNo, productNo as productNo, num as num, unit as unit, maxNum as maxNum, productType as type, DATE_FORMAT(createTime,'%Y-%m-%d') as createTime, DATE_FORMAT(completeDate,'%Y-%m-%d') as completeDate, remark as remark, blanking as blanking, frontFm as frontFm, frontCheck as frontCheck,
backBigAssist as backBigAssist,
backCenterAssist as backCenterAssist, assist as assist, inspection as inspection from t_Order_Product WHERE 1=1
AND orderNo = #{orderNo}
AND productNo = #{pmNo}
AND productType = #{type}
and date_format(completeDate,'%Y-%m-%d') >= #{completeStartDate}
and date_format(createTime,'%Y-%m-%d') >= #{createStartDate}
and date_format(completeDate,'%Y-%m-%d') <= #{completeStartEnd}
and date_format(createTime,'%Y-%m-%d') <= #{createEndDate}
and customerId = #{customerId}
and isDelete = 0 order by createTime desc

mapper.update:
update  t_Order_Product
set isDelete = 1
where id = #{id}

mapper.select:
SELECT
productType as type,
sum(num) as num
from t_Order_Product
WHERE 1=1

    AND orderNo LIKE '%${orderNo}%'


    AND productNo LIKE '%${pmNo}%'


    AND productType = #{type}


    and date_format(completeDate,'%Y-%m-%d') >= #{completeStartDate}


    and date_format(createTime,'%Y-%m-%d') >= #{createStartDate}


    and date_format(completeDate,'%Y-%m-%d') <= #{completeStartEnd}


    and date_format(createTime,'%Y-%m-%d') <= #{createEndDate}


    and customerId = #{customerId}

and isDelete = 0 GROUP BY productType
