mapper.select:
select o.id, o.orderNo, o.productNo, o.billNo, o.groupNmae, o.pmType, o.pmCheckItem, o.distributionNum num, p.assignedNum, date_format(o.createTime, '%Y-%m-%d') billDate from t_PMout_Bill o LEFT JOIN t_Order_Product p ON o.orderNo = p.orderNo and o.productNo = p.productNo where o.isDelete = 0
and o.orderNo = #{orderNo,jdbcType=VARCHAR}
and o.productNo = #{productNo,jdbcType=VARCHAR}
and o.billNo = #{billNo,jdbcType=VARCHAR}
order by o.createTime desc

select o.orderNo, o.productNo, o.unit, o.num, o.maxNum, o.assignedNum, o.notAssignNum from t_Order_Product o WHERE o.isDelete = 0
and o.orderNo = #{orderNo,jdbcType=VARCHAR}
and o.productNo = #{productNo,jdbcType=VARCHAR}
and o.customerName = #{customerName,jdbcType=VARCHAR}
and o.productNo not in (select DISTINCT productNo from t_ProductPM where isDelete=0 and status = 1)
and o.productNo in (select DISTINCT productNo from t_ProductPM where isDelete=0 and status = 1)
order by o.createTime desc

select count(1) from
  (select DISTINCT a.pmNo from t_PMout_Bill_Detail a left join t_PMout_Bill b on a.billId = b.id
    where b.orderNo= #{orderNo,jdbcType=VARCHAR}
    and b.productNo = #{productNo,jdbcType=VARCHAR}
    and b.type =1
    group by a.pmNo) aa

select * from ( (select i.pmId id, i.pmNo, i.pmName name, i.supplier, i.remark, i.inDate date, i.createTime dateTime, i.operator, i.backNum num, i.num inNum, null AS outNum, null AS orderNo, null AS productNo from t_PMIn i where i.isDelete = 0
and i.pmName like '%${pmName}%'
and i.pmNo = #{pmNo}
and i.operator like '%${operator}%'
and i.inDate >= #{startDate}
and i.inDate <= #{endDate}
) UNION ALL ( select o.pmId id, o.pmNo, o.pmName name, null AS supplier, o.remark, o.outDate date, o.createTime dateTime, o.operator, o.backNum num, null AS inNum, o.num AS outNum, b.orderNo AS orderNo, b.productNo AS productNo from t_PMout_Bill b LEFT JOIN t_PMout_Bill_Detail o on b.id = o.billId where b.isDelete = 0 and (b.type = 1 or b.type = 3)
and o.pmName like '%${pmName}%'
and o.pmNo = #{pmNo}
and o.operator like '%${operator}%'
and o.outDate >= #{startDate}
and o.outDate <= #{endDate}
and b.orderNo = #{orderNo}
) ) a ORDER BY a.date, a.dateTime desc

select i.pmId id, i.pmNo, i.pmName name, i.supplier, i.remark, i.inDate date, i.operator, i.backNum num, i.num inNum, null AS outNum, null AS orderNo, null AS productNo from t_PMIn i where i.isDelete = 0
and i.pmName like '%${pmName}%'
and i.pmNo = #{pmNo}
and i.operator like '%${operator}%'
and i.inDate >= #{startDate}
and i.inDate <= #{endDate}
order by i.inDate,i.createTime desc

select o.pmId id, o.pmNo, o.pmName name, null AS supplier, o.remark, o.outDate date, o.operator, o.backNum num, null AS inNum, o.num AS outNum, b.orderNo AS orderNo, b.productNo AS productNo from t_PMout_Bill b LEFT JOIN t_PMout_Bill_Detail o on b.id = o.billId where b.isDelete = 0 and (b.type = 1 or b.type = 3)
and o.pmName like '%${pmName}%'
and o.pmNo = #{pmNo}
and o.operator like '%${operator}%'
and o.outDate >= #{startDate}
and o.outDate <= #{endDate}
and b.orderNo = #{orderNo}
order by o.outDate,o.createTime desc

select o.pmId id, o.pmNo, o.pmName name, o.remark outInfo, o.outDate date, o.operator, o.num AS outNum from t_PMout_Bill b LEFT JOIN t_PMout_Bill_Detail o on b.id = o.billId where b.isDelete = 0 and b.type = 3
and o.pmNo = #{pmNo}
and o.operator like '%${operator}%'
and o.outDate >= #{startDate}
and o.outDate <= #{endDate}
order by o.outDate,o.createTime desc
